    width:100%;
    border-bottom:1px solid #3E6386;
    padding-bottom:15px;
}
.gj-div-line-1:after {
    content: '';
    width: 150px;
    height: 6px;
    background: #59B7A9;
    position: absolute;
    bottom: -5px;
    left: calc(50% - 75px);
    box-shadow: 0px 0px 14px #59B7A9;
    border-radius: 0px 0px 3px 3px;
}

/*** ANIMATIONS ***/
.gj-div-line-1 {
    width:0;
    margin:0 auto;
    opacity:0;
    -webkit-animation: lineextend 5s 0s linear forwards;
    -moz-animation: lineextend 5s 0s linear forwards;
    -ms-animation: lineextend 5s 0s linear forwards;
    -o-animation: lineextend 5s 0s linear forwards;
    animation: lineextend 5s 0s linear forwards;
}
@-webkit-keyframes lineextend {
    from {
        width:0;
        opacity:0;
    }
    to {
        width:100%;
        opacity:1;
    }
}
@keyframes lineextend {
    from {
        width:0;
        opacity:0;
    }
    to {
        width:100%;
        opacity:1;
    }
}

.gj-div-line-1:after {
        background:#59B7A9;
        box-shadow:0px 0px 4px #59B7A9;
        -webkit-animation: glow 5s 0s infinite alternate;
        -moz-animation: glow 5s 0s infinite alternate;
        -ms-animation: glow 5s 0s infinite alternate;
        -o-animation: glow 5s 0s infinite alternate;
        animation: glow 5s 0s infinite alternate;
}
@-webkit-keyframes glow {
    0% {
        background:#59B7A9;
        box-shadow:0px 0px 4px #59B7A9;
    }
    50% {
        background: #00ffd9;
        box-shadow:0px 0px 15px #00ffd9;
    }
    100% {
        background:#59B7A9;
        box-shadow:0px 0px 4px #59B7A9;
    }
}
@keyframes glow {
    0% {
        background:#59B7A9;
        box-shadow:0px 0px 4px #59B7A9;
    }
    50% {
        background: #00ffd9;
        box-shadow:0px 0px 15px #00ffd9;
    }
    100% {
        background:#59B7A9;
        box-shadow:0px 0px 4px #59B7A9;
    }
}

.benefits-inner p,
.benefits-grid p {
    color: #92a3b3;
}
/*** Benefits Animated SVG Size Fix ***/
.benefits-grid-ico {
    width:70px!important;
}
.benefits-grid-ico object embed {
    width: 70px!important;
    height: 70px!important;
}

/*** ADOBE Fix Bottom Margin to fit image on screen ***/
#adobe #benefits-grid-2 .wf-cell {
    margin-bottom: 90px;
}

/*** FIX MAIN BOTTOM PADDING ***/
#main {
    padding-bottom:0!important;
}
